  include: {
    category: true;
    subCategory: true;
    tags: true;
  };
}>;

// Define the frontend type with serialized dates
export type SerializedCourse = Omit<CourseWithRelations, "postDate"> & {
  postDate: string;
};

export const loader: LoaderFunction = async ({ request }) => {
  const url = new URL(request.url);
  const search = url.searchParams.get("search") || "";
  const categoryId = url.searchParams.get("categoryId") || undefined;
  const subCategoryId = url.searchParams.get("subCategoryId") || undefined;
  const isFree = url.searchParams.get("isFree");
  const tagId = url.searchParams.get("tagId") || undefined;
  const startDate = url.searchParams.get("startDate") || undefined;
  const endDate = url.searchParams.get("endDate") || undefined;
  const sort = url.searchParams.get("sort") || "postDate-desc";

  // Build dynamic Prisma query
  const where: Prisma.CourseWhereInput = {
    title: search ? { contains: search, mode: "insensitive" } : undefined,
    categoryId: categoryId || undefined,
    subCategoryId: subCategoryId || undefined,
    isFree: isFree === "yes" ? true : isFree === "no" ? false : undefined,
    tags: tagId ? { some: { id: tagId } } : undefined,
    postDate: {
      gte: startDate ? new Date(startDate) : undefined,
      lte: endDate ? new Date(endDate) : undefined,
    },
  };

  // Handle sorting
  const orderBy: Prisma.CourseOrderByWithRelationInput = (() => {
    switch (sort) {
      case "title-asc":
        return { title: "asc" };
      case "title-desc":
        return { title: "desc" };
      case "postDate-asc":
        return { postDate: "asc" };
      case "postDate-desc":
      default:
        return { postDate: "desc" };
    }
  })();

  // Fetch courses
  const courses = await prisma.course.findMany({
    where,
    orderBy,
    include: {
      category: true,
      subCategory: true,
      tags: true,
    },
  });

  // Fetch dropdown data
  const allCategories = await prisma.category.findMany({
    include: { subCategories: true },
  });
