  // Serialize the dates to strings for JSON compatibility
  const serializedCourses: SerializedCourse[] = courses.map((course) => ({
    ...course,
    postDate: course.postDate.toISOString(),
  }));

  return json({ courses: serializedCourses, allCategories, allTags });
};

