                <label key={tag.id} className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    name="tags"
                    value={tag.id}
                    defaultChecked={course.tags.some((t) => t.id === tag.id)}
                    className="text-indigo-600 focus:ring-indigo-500"
