      >
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <div className="relative">
            <label
              htmlFor="search"
              className="block text-xs font-semibold text-gray-500 uppercase tracking-wider mb-1"
            >
              Search
            </label>
            <div className="relative">
              <input
                type="text"
                name="search"
                id="search"
                defaultValue={searchParams.get("search") || ""}
                className="w-full bg-white border border-gray-300 text-gray-900 placeholder-gray-500 rounded-md px-4 py-2 shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-all"
                placeholder="Search courses..."
              />
            </div>
          </div>

          <div>
            <label
              htmlFor="categoryId"
              className="block text-xs font-semibold text-gray-500 uppercase tracking-wider mb-1"
            >
              Category
            </label>
            <select
              name="categoryId"
              id="categoryId"
              key={searchParams.get("categoryId")}
              defaultValue={searchParams.get("categoryId") || ""}
              className="w-full bg-white border border-gray-300 text-gray-900 placeholder-gray-500 rounded-md px-4 py-2 shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-all"
            >
              <option value="">All Categories</option>
              {allCategories.map((category) => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label
              htmlFor="subCategoryId"
              className="block text-xs font-semibold text-gray-500 uppercase tracking-wider mb-1"
            >
              SubCategory
            </label>
            <select
              name="subCategoryId"
              id="subCategoryId"
              key={searchParams.get("subCategoryId")}
              defaultValue={searchParams.get("subCategoryId") || ""}
              disabled={!selectedCategoryId}
              className="w-full bg-white border border-gray-300 text-gray-900 placeholder-gray-500 rounded-md px-4 py-2 shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-all"
            >
              <option value="">All SubCategories</option>
              {filteredSubCategories.map((subCategory) => (
                <option key={subCategory.id} value={subCategory.id}>
                  {subCategory.name}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label
              htmlFor="isFree"
              className="block text-xs font-semibold text-gray-500 uppercase tracking-wider mb-1"
            >
              Type
            </label>
            <select
              name="isFree"
              id="isFree"
              key={searchParams.get("isFree")}
              defaultValue={searchParams.get("isFree") || ""}
              className="w-full bg-white border border-gray-300 text-gray-900 placeholder-gray-500 rounded-md px-4 py-2 shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-all"
            >
              <option value="all">All</option>
