    id: string;
    title: string;
    isFree: boolean;
    postDate: string;
    categoryId: string;
    subCategoryId: string;
    tags: { id: string; name: string }[];
  };
  allCategories: Array<{
    id: string;
    name: string;
    subCategories: { id: string; name: string }[];
  }>;
  allTags: { id: string; name: string }[];
};

export const loader: LoaderFunction = async ({ params }) => {
  const { courseId } = params;

  const course = await prisma.course.findUnique({
    where: { id: courseId },
    include: { tags: true },
  });

  if (!course) {
    throw new Response("Course not found", { status: 404 });
  }

  const allCategories = await prisma.category.findMany({
    include: { subCategories: true },
    orderBy: { name: "asc" },
  });

  const allTags = await prisma.tag.findMany({
    orderBy: { name: "asc" },
  });

  return json<LoaderData>({
    course: {
      ...course,
      postDate: course.postDate.toISOString().split("T")[0],
    },
    allCategories,
    allTags,
  });
};

type ActionData = {
  errors?: {
    title?: string;
    categoryId?: string;
    subCategoryId?: string;
    postDate?: string;
  };
};

export const action: ActionFunction = async ({ request, params }) => {
  const { courseId } = params;
  const formData = await request.formData();

  const title = formData.get("title")?.toString();
  const categoryId = formData.get("categoryId")?.toString();
  const subCategoryId = formData.get("subCategoryId")?.toString();
  const postDate = formData.get("postDate")?.toString();
  const isFree = formData.get("isFree") === "true";
  const tagIds = formData.getAll("tags").map((tag) => tag.toString());

  const errors: ActionData["errors"] = {};
  if (!title) errors.title = "Title is required";
  if (!categoryId) errors.categoryId = "Category is required";
  if (!subCategoryId) errors.subCategoryId = "SubCategory is required";
  if (!postDate) errors.postDate = "Post Date is required";

  if (Object.keys(errors).length > 0) {
    return json<ActionData>({ errors });
  }

  await prisma.course.update({
    where: { id: courseId },
    data: {
      title,
      isFree,
      postDate: new Date(postDate),
      categoryId,
      subCategoryId,
      tags: {
        set: [],
        connect: tagIds.map((id) => ({ id })),
      },
    },
  });

  return redirect("/courses");
};

